package test;

public class TestIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//成绩变量，如果成绩大于等于60，输出“课程考核合格”；否则，输出不合格
		int score=64;
		if (score>=60) {
			//满足条件
			System.out.println("课程考核合格");
		}else {
			//不满足条件
			System.out.println("不合格");
		}

	}

}
